package com.cakeshop.bean;

import java.io.Serializable;
import java.sql.Date;

import javax.persistence.Column;
import javax.persistence.Entity;
import javax.persistence.GeneratedValue;
import javax.persistence.GenerationType;
import javax.persistence.Id;
import javax.persistence.JoinColumn;
import javax.persistence.OneToOne;
import javax.persistence.Table;

@Entity
@Table(name="video")
public class VideoBean implements Serializable{

	private static final long serialVersionUID = -5813264827007873950L;
	private int videoId;
	private String videoUrl;
	private String videoTitle;
	private int videoCommentNum;
	private int videoZanNum;
	private Date videoDate;
	private String videoPic;
	private String videoType;
	
	public VideoBean() {
		super();
		// TODO Auto-generated constructor stub
	}

	public VideoBean(int videoId, String videoUrl, String videoTitle, int videoCommentNum, int videoZanNum,
			Date videoDate, String videoPic, String videoType) {
		super();
		this.videoId = videoId;
		this.videoUrl = videoUrl;
		this.videoTitle = videoTitle;
		this.videoCommentNum = videoCommentNum;
		this.videoZanNum = videoZanNum;
		this.videoDate = videoDate;
		this.videoPic = videoPic;
		this.videoType = videoType;
	}



	@Id
	@GeneratedValue(strategy = GenerationType.AUTO)
	@Column(name="video_id")                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
	public int getVideoId() {
		return videoId;
	}
	public void setVideoId(int videoId) {
		this.videoId = videoId;
	}
	@Column(name="video_url") 
	public String getVideoUrl() {
		return videoUrl;
	}
	public void setVideoUrl(String videoUrl) {
		this.videoUrl = videoUrl;
	}
	@Column(name="video_type") 
	public String getVideoType() {
		return videoType;
	}

	public void setVideoType(String videoType) {
		this.videoType = videoType;
	}

	@Column(name="video_title") 
	public String getVideoTitle() {
		return videoTitle;
	}
	public void setVideoTitle(String videoTitle) {
		this.videoTitle = videoTitle;
	}
	@Column(name="video_comment_num") 
	public int getVideoCommentNum() {
		return videoCommentNum;
	}
	public void setVideoCommentNum(int videoCommentNum) {
		this.videoCommentNum = videoCommentNum;
	}
	@Column(name="video_zan_num") 
	public int getVideoZanNum() {
		return videoZanNum;
	}
	public void setVideoZanNum(int videoZanNum) {
		this.videoZanNum = videoZanNum;
	}
	@Column(name="video_date") 
	public Date getVideoDate() {
		return videoDate;
	}
	
	public void setVideoDate(Date videoDate) {
		this.videoDate = videoDate;
	}
	@Column(name="video_pic") 
	public String getVideoPic() {
		return videoPic;
	}
	public void setVideoPic(String videoPic) {
		this.videoPic = videoPic;
	}

	@Override
	public String toString() {
		return "VideoBean [videoId=" + videoId + ", videoUrl=" + videoUrl + ", videoTitle=" + videoTitle
				+ ", videoCommentNum=" + videoCommentNum + ", videoZanNum=" + videoZanNum + ", videoDate=" + videoDate
				+ ", videoPic=" + videoPic + "]";
	}
		
	
}
